document.addEventListener("DOMContentLoaded",(()=>{const t=document.querySelectorAll(".tab-button"),e=document.querySelectorAll(".tab-panel");t.forEach((a=>{a.addEventListener("click",(()=>{const c=a.getAttribute("data-tab");t.forEach((t=>t.classList.remove("active"))),e.forEach((t=>t.classList.remove("active"))),a.classList.add("active"),document.querySelector(`.tab-panel[data-tab="${c}"]`).classList.add("active")}))}))}));